/**
 * File: frontend/src/pages/Login.js
 * Login Page Component
 */
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Form, Button, Alert } from 'react-bootstrap';
import { authAPI } from '../services/apiService';
import { saveAuthData } from '../utils/authUtils';
import { toast } from 'react-toastify';

function Login() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [rememberMe, setRememberMe] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await authAPI.login(formData);
      const { token, userId, email, name } = response.data;

      // L∆∞u th√¥ng tin ƒëƒÉng nh·∫≠p
      saveAuthData(token, { userId, email, name });

      toast.success('ƒêƒÉng nh·∫≠p th√†nh c√¥ng!');
      navigate('/');
    } catch (error) {
      const errorMessage = error.response?.data?.message || 'ƒêƒÉng nh·∫≠p th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i!';
      setError(errorMessage);
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="py-5">
      <Container>
        <Row className="justify-content-center">
          <Col md={6} lg={5}>
            <Card className="border-0 shadow-sm rounded-4 p-4">
              <Card.Body>
                <div className="text-center mb-4">
                  <h2 className="section-title">üîë ƒêƒÉng nh·∫≠p</h2>
                  <p className="text-muted">Ch√†o m·ª´ng b·∫°n tr·ªü l·∫°i!</p>
                </div>

                {error && <Alert variant="danger">{error}</Alert>}

                <Form onSubmit={handleSubmit}>
                  <Form.Group className="mb-3">
                    <Form.Label className="fw-semibold">Email</Form.Label>
                    <Form.Control
                      type="email"
                      name="email"
                      placeholder="Nh·∫≠p email c·ªßa b·∫°n"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label className="fw-semibold">M·∫≠t kh·∫©u</Form.Label>
                    <Form.Control
                      type="password"
                      name="password"
                      placeholder="Nh·∫≠p m·∫≠t kh·∫©u"
                      value={formData.password}
                      onChange={handleChange}
                      required
                    />
                  </Form.Group>

                  <div className="d-flex justify-content-between align-items-center mb-3">
                    <Form.Check
                      type="checkbox"
                      label="Ghi nh·ªõ ƒëƒÉng nh·∫≠p"
                      checked={rememberMe}
                      onChange={(e) => setRememberMe(e.target.checked)}
                    />
                    <Link to="/forgot-password" className="text-decoration-none">
                      Qu√™n m·∫≠t kh·∫©u?
                    </Link>
                  </div>

                  <Button
                    variant="primary"
                    type="submit"
                    className="w-100"
                    disabled={loading}
                  >
                    {loading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2"></span>
                        ƒêang ƒëƒÉng nh·∫≠p...
                      </>
                    ) : (
                      <>
                        <i className="bi bi-box-arrow-in-right me-2"></i>
                        ƒêƒÉng nh·∫≠p
                      </>
                    )}
                  </Button>
                </Form>

                <div className="text-center mt-4">
                  <p className="mb-0">
                    Ch∆∞a c√≥ t√†i kho·∫£n?{' '}
                    <Link to="/register" className="fw-bold">
                      ƒêƒÉng k√Ω ngay
                    </Link>
                  </p>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </section>
  );
}

export default Login;
